<?phpclass Clients_Model extends CI_Model {    public function __construct() {        $this->load->database();    }    public function get_clients($userID = FALSE) {        if ($userID === FALSE) {            $query = $this->db->get('users');            return $query->result_array();        }        $query = $this->db->get_where('users', array('id' => $userID));                return $query->row_array();    }    public function set_clients($data) {        return $this->db->insert('users', $data);    }}