<?phpclass Clients_Model extends CI_Model {    public function get_clients($userID = FALSE) {        if ($userID === FALSE) {            $query = $this->db->get('clients');            return $query->result_array();        }        $query = $this->db->get_where('clients', array('userID' => $userID));        return $query->row();    }    public function get_clients_email($email) {        $query = $this->db->get_where('clients', array('email' => $email));        return $query->row();    }    public function set_clients($data) {        return $this->db->insert('clients', $data);    }    public function get_client_login($userID = null) {        if ($userID === null) {            $this->db->select('*');            $this->db->from('clientlogins');            $this->db->join('clients', 'clients.userID = clientlogins.userID');            $query = $this->db->get();                        return $query->result_array();        } else {            $this->db->select('*');            $this->db->from('clientlogins');            $this->db->join('clients', 'clients.userID = clientlogins.userID');            $this->db->where('clients.userID', $userID);            $query = $this->db->get();            return $query->row();        }    }    public function set_clients_login($data) {        return $this->db->insert('clientlogins', $data);    }    public function activate_client($userID) {        $this->db->set('verified', '1');        $this->db->where('userID', $userID);        return $this->db->update('clientlogins');    }}