<?phpclass Clients_Model extends CI_Model {    public function get_clients($userID = FALSE) {        if ($userID === FALSE) {            $this->db->select('*');            $this->db->from('clients');            $this->db->join('clientsinformation', 'clients.userID = clientsinformation.userID');            $this->db->order_by("lastName", "asc");            $query = $this->db->get();            return $query->result_array();        }        $this->db->select('*');        $this->db->from('clients');        $this->db->join('clientsinformation', 'clients.userID = clientsinformation.userID');        $this->db->where('clients.userID', $userID);        $query = $this->db->get();        return $query->row();    }    public function get_new_clients() {        $query = $this->db->get_where('clients', array('newClient' => 1));        return $query->result_array();    }    public function get_clients_email($email) {        $query = $this->db->get_where('clients', array('email' => $email));        return $query->row();    }        public function get_clientsSearch($name) {        $this->db->select('*');        $this->db->from('clients');        $this->db->like('lastName', $name);        $query = $this->db->get();                return $query->result_array();    }        public function count_clients() {        return $this->db->count_all('clients');    }    public function set_clients($data) {        $this->db->insert('clients', $data);        $userID = $this->db->insert_id();        $planData = array(            'userID' => $userID        );        $this->db->insert('clientsinformation', $planData);                return true;    }    public function get_client_login($userID = null) {        if ($userID === null) {            $this->db->select('*');            $this->db->from('clientlogins');            $this->db->join('clients', 'clients.userID = clientlogins.userID');            $query = $this->db->get();            return $query->result_array();        } else {            $this->db->select('*');            $this->db->from('clientlogins');            $this->db->join('clients', 'clients.userID = clientlogins.userID');            $this->db->where('clients.userID', $userID);            $query = $this->db->get();            return $query->row();        }    }    public function set_clients_login($data) {        return $this->db->insert('clientlogins', $data);    }    public function activate_client($userID) {        $this->db->set('verified', '1');        $this->db->where('userID', $userID);        return $this->db->update('clientlogins');    }    public function update_details($data, $userID) {        $this->db->where('userID', $userID);        $this->db->update('clients', $data);    }    public function update_additional_details($data, $userID, $new = null) {        if ($new != null) {            $planData = array(                'newClient' => 0            );            $this->db->where('userID', $userID);            $this->db->update('clients', $planData);        }        $this->db->where('userID', $userID);        $this->db->update('clientsinformation', $data);    }}